import React, { useState } from 'react';
import './Section.scss';
import { Image, Container, Row, Col, Button, Collapse } from 'react-bootstrap';

type Props = {
    title: string;
    colour: string;
    layout: Number;
};

export const Section: React.FC<Props> = props => {
    // const [open, setOpen] = useState(false);
    let content;
    switch (props.layout) {
        case 1:
            content = <Col className="content">{props.children}</Col>;
            break;
        case 2:
            content = (
                <Col className="content">
                    <Row noGutters>
                        <Col>{props.children}</Col>
                        <Col></Col>
                    </Row>
                </Col>
            );
            break;
    }
    return (
        <div className="sectionContainer">
            <Row noGutters className="sectionRow">
                <Col lg="2" className="title">
                    <h3>{props.title}</h3>
                </Col>
                <Col className="content">{props.children}</Col>
            </Row>
        </div>
    );
};
